package exam;

public class Ex03_PrintExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean stop = true;
		boolean state = false;
		
		String str1 = "는";
		String str2 = "이다";
		
		System.out.println("푸바오" + str1 + " 자이언트 판다 " + str2 + "[" + stop +"]");
		System.out.println("원숭이 엉덩이" + str1 + " 노랗다" + "[" + state +"]");
		System.out.println("나" + str1 + " 여자다 " + "[" + stop +"]");
		System.out.println("나" + str1 + " 열살" + str2 +"[" + state +"]");
		System.out.println("사람의 다리" + str1 + " 3개다" +"[" + state +"]");
		System.out.println("현재 시간은 오전 9시" + str2 +"[" + state +"]");
		System.out.println("나" + str1 + " 프로그래밍이 재밌다" + "[" + state +"]");
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
